		g2_Origin = #{g2_Origin}, g2_Memo = #{g2_Memo, jdbcType=VARCHAR}, g2_Opentime = #{g2_Opentime},
		g2_Minpay =  #{g2_Minpay}
WHERE g2_Num = #{g2_Num}

UPDATE giup3 
