mapper.select:
SELECT NVL(COUNT(*), 0) FROM userFaq uf JOIN faqCategory fc ON uf.ca_Num=fc.ca_Num JOIN admin ad ON uf.admin_Id=ad.admin_Id
u.ca_Num=#{ca_Num}
AND (faq_Title LIKE '%' || #{searchValue} || '%' OR faq_Content LIKE '%' || #{searchValue} || '%')

SELECT * FROM (
  SELECT ROWNUM rnum, tb.* FROM(
     SELECT faq_Num, uf.ca_Num, ca_class, admin_Id, faq_Title, faq_Content, faq_Count
            FROM userFaq uf
            JOIN faqCategory fc ON uf.ca_Num=fc.ca_Num
            JOIN admin ad ON uf.admin_Id=ad.admin_Id
            
              
                uf.ca_Num = #{ca_Num}
              
              
               AND (faq_Title LIKE '%' || #{searchValue} || '%' OR
               faq_Content LIKE '%' || #{searchValue} || '%')
              
            
            ORDER BY faq_Num DESC
            
  ) tb WHERE ROWNUM <= #{end}
)WHERE rnum >= #{start}
