mapper.select:
		SELECT * FROM (
			SELECT ROWNUM rnum, tb.* FROM (
				SELECT hak, name, 
				TO_CHAR(birth, 'YYYY-MM-DD') birth,
				kor, eng, mat, (kor+eng+mat) tot,
				(kor+eng+mat)/3 ave
				FROM score
				
					
						
					
				
				ORDER BY hak ASC

			) tb WHERE ROWNUM <= #{end}
		) WHERE rnum >= #{start}

mapper.update:
UPDATE score SET name = #{name}, birth = #{birth},
	kor = #{kor}, eng = #{eng}, mat = #{mat}
	WHERE hak = #{hak}

mapper.delete:
DELETE FROM score WHERE
hak IN

	#{hak}
